// ...
pub async fn create_todo(state: web::Data<Clients>, info: web::Json<TodoCard>) -> impl Responder { let todo_card = adapter::todo_json_to_db(info, uuid::Uuid::new_v4()); match put_todo(state.dynamo.clone(), todo_card) { None => { error!("Failed to create todo card"); HttpResponse::BadRequest().body("Failed to create todo card") } Some(id) => HttpResponse::Created() .content_type("application/json") .json(TodoIdResponse::new(id)) }
