// ... pub fn create_table() { let client = client(); let list_tables_input: ListTablesInput = Default::default(); match client.list_tables(list_tables_input).sync() { Ok(list) => { match list.table_names { Some(table_vec) => { if table_vec.len() > 0 { error!("Table already exists and has more then one item"); } else { create_table_input() } } None => create_table_input(), }; } Err(_) => { create_table_input(); } }
