#[table_name = "auth_user"]
pub struct User { #[cfg(test)] pub email: String, #[cfg(not(test))] email: String, #[cfg(test)] pub id: uuid::Uuid, #[cfg(not(test))] id: uuid::Uuid, #[cfg(test)] pub password: String, #[cfg(not(test))] password: String, #[cfg(test)] pub expires_at: chrono::NaiveDateTime, #[cfg(not(test))] expires_at: chrono::NaiveDateTime,
}
